widgets.script:
if (get("prop_switch") ==0) {
  send("midi:loopMIDI","/note",1,3,1)
  send("midi:loopMIDI","/note",1,3,0)
} else {
  send("midi:loopMIDI","/note",1,3,1)
  send("midi:loopMIDI","/note",1,3,0)
  send("midi:loopMIDI","/note",1,24,get("prop_switch"))
}

if (get("AUTO FOCUS")==1){
send("127.0.0.1:8002","/"+get("camera_id")+"/focus_auto",get("pan_speed"),get("tilt_speed"))

if (get("camera_id") ==0) {
set("led_af_1",1)
set("led_af_2",1)
set("led_af_3",1)
} else if (get("camera_id") ==1) {
set("led_af_1",1)
} else if (get("camera_id") ==2) {
set("led_af_2",1)
} else if (get("camera_id") ==3) {
set("led_af_3",1)
}
} else {
if (get("camera_id") ==0) {
set("led_af_1",0)
set("led_af_2",0)
set("led_af_3",0)
} else if (get("camera_id") ==1) {
set("led_af_1",0)
} else if (get("camera_id") ==2) {
set("led_af_2",0)
} else if (get("camera_id") ==3) {
set("led_af_3",0)
}
}

if (get("MANUAL FOCUS")==1){
send("127.0.0.1:8002","/"+get("camera_id")+"/focus_manual",get("pan_speed"),get("tilt_speed"))

if (get("camera_id") ==0) {
set("led_af_1",0)
set("led_af_2",0)
set("led_af_3",0)
} else if (get("camera_id") ==1) {
set("led_af_1",0)
} else if (get("camera_id") ==2) {
set("led_af_2",0)
} else if (get("camera_id") ==3) {
set("led_af_3",0)
}
} else {
if (get("camera_id") ==0) {
set("led_af_1",1)
set("led_af_2",1)
set("led_af_3",1)
} else if (get("camera_id") ==1) {
set("led_af_1",1)
} else if (get("camera_id") ==2) {
set("led_af_2",1)
} else if (get("camera_id") ==3) {
set("led_af_3",1)
}
}
